/**
 * Get site setting by key
 * @param {string} key - Setting key
 * @param {any} defaultValue - Default value if not found
 * @returns {any} Setting value or default
 */
export const getSiteSetting = (key, defaultValue = null) => {
  return siteSettings?.[key] ?? defaultValue;
};

/**
 * Get sales representative by state code
 * @param {string} stateCode - Two-letter state code (e.g., "TX", "CA")
 * @returns {object|null} Sales rep object or null
 */
export const getRepByState = (stateCode) => {
  return salesReps?.find(rep => 
    rep.states_covered?.includes(stateCode)
  ) ?? null;
};

/**
 * Get page content by slug and section
 * @param {string} pageSlug - Page slug (e.g., "home", "about")
 * @param {string} sectionKey - Section key (e.g., "hero", "cta")
 * @returns {object|null} Page content object or null
 */
export const getPageContent = (pageSlug, sectionKey) => {
  return pageContent?.find(content => 
    content.page_slug === pageSlug && content.section_key === sectionKey
  ) ?? null;
};

